export type MapColor = {
	/**
	 * ## Map Color
	 *
	 * Sets the color of the block when rendered to a map.
	 * The color is represented as a hex value in the format "#RRGGBB".
	 * May also be expressed as an array of `"[R, G, B]"` from 0 to 255.
	 *
	 * @default '#RRGGBB'
	 */
	map_color?: string
}
